import React, { useState } from 'react';
import './App.css';
import lowScoreSound from './sounds/low-score.mp3';
import backgroundVideo from './videos/background.mp4';

const quizData = [
    { question: 'Lab mo ba ako?', options: ['Oo', 'Hindi', 'Minsan', 'Secret'], correctAnswer: 'Oo' },
    { question: 'What is 2 + 2?', options: ['3', '4', '5', '6'], correctAnswer: '4' },
    { question: 'Who wrote "Romeo and Juliet"?', options: ['William Shakespeare', 'Charles Dickens', 'Jane Austen', 'Mark Twain'], correctAnswer: 'William Shakespeare' },
    { question: 'Meow meow?', options: ['Tarantado ka ba', 'Baliw', 'Inutil', 'Supot'], correctAnswer: 'Baliw' }
];

function App() {
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showResult, setShowResult] = useState(false);
    const [score, setScore] = useState(0);

    const handleAnswerClick = (selectedAnswer) => {
        if (selectedAnswer === quizData[currentQuestion].correctAnswer) {
            setScore(score + 1);
        }
        if (currentQuestion < quizData.length - 1) {
            setCurrentQuestion(currentQuestion + 1);
        } else {
            // Check the score and play the low-score sound if necessary
            if (score + (selectedAnswer === quizData[currentQuestion].correctAnswer ? 1 : 0) <= 2) {
                const lowScoreAudio = new Audio(lowScoreSound);
                lowScoreAudio.play();
            }
            setShowResult(true);
        }
    };

    const restartQuiz = () => {
        setCurrentQuestion(0);
        setShowResult(false);
        setScore(0);
    };

    return (
        <div className="container">
            <h1>Quiz App</h1>
            {showResult ? (
                <div className="result">
                    <p>Your score: {score} out of {quizData.length}</p>
                    <button onClick={restartQuiz}>Restart Quiz</button>
                </div>
            ) : (
                <>
                    <div className="question">
                        <p>{quizData[currentQuestion].question}</p>
                    </div>
                    <div className="options">
                        {quizData[currentQuestion].options.map((option, index) => (
                            <div key={index} className="option" onClick={() => handleAnswerClick(option)}>
                                {option}
                            </div>
                        ))}
                    </div>
                </>
            )}
        </div>
    );
}

export default App;
